function addNote(newNote){
  const [notes,setNotes]=useState([]);
  setNotes(prevNotes =>{
    return [...prevNotes,newNote];
  })

}
function App() {
  return (
    <div>
      <Header />
      <CreateArea onAdd={addNote} />
      <Note key={1} title="Note title" content="Note content" />
      <Footer />
    </div>
  );
}

export default App;
